sys_variable_value.value:
(function execute(inputs, outputs) {

if (!Object.assign) {
  Object.defineProperty(Object, 'assign', {
    enumerable: false,
    configurable: true,
    writable: true,
    value: function(target) {
      'use strict';
      if (target === undefined || target === null) {
        throw new TypeError('Cannot convert first argument to object');
      }

      var to = Object(target);
      for (var i = 1; i < arguments.length; i++) {
        var nextSource = arguments[i];
        if (nextSource === undefined || nextSource === null) {
          continue;
        }
        nextSource = Object(nextSource);

        var keysArray = Object.keys(Object(nextSource));
        for (var nextIndex = 0, len = keysArray.length; nextIndex < len; nextIndex++) {
          var nextKey = keysArray[nextIndex];
          var desc = Object.getOwnPropertyDescriptor(nextSource, nextKey);
          if (desc !== undefined && desc.enumerable) {
            to[nextKey] = nextSource[nextKey];
          }
        }
      }
      return to;
    }
  });
}  


var response = JSON.parse(inputs.response)

var schema = response.schema
var preset = response.data
var presetFields = response.presetFields

gs.addInfoMessage("schema " + schema)
gs.addInfoMessage("preset" + preset)
gs.addInfoMessage("presetFields" + presetFields)

var _device_preset = JSON.stringify(preset)
var _device_preset_fields = JSON.stringify(presetFields)
var _device_schema_context = schema.contextURL
var _device_schema_type = schema.type
var _device_schema_field3_context = schema.fields[3].context.context
var _device_schema_field3_type = schema.fields[3].context.type
var _device_schema_field4_context = schema.fields[4].context.context
var _device_schema_field4_type = schema.fields[4].context.type
var _device_schema_field5_context = schema.fields[5].context.context
var _device_schema_field5_type = schema.fields[5].context.type

var device_preset = _device_preset
var preset = JSON.parse(device_preset)
var device_presetFields = _device_preset_fields
var presetFields = JSON.parse(device_presetFields)

var device_schema_context = _device_schema_context
var device_schema_type = _device_schema_type
var device_schema_field3_context = _device_schema_field3_context
var device_schema_field3_type = _device_schema_field3_type
var device_schema_field4_context = _device_schema_field4_context
var device_schema_field4_type = _device_schema_field4_type
var device_schema_field5_context = _device_schema_field5_context
var device_schema_field5_type = _device_schema_field5_type

var body = {
    "document": {
        "type": device_schema_type,
        "@context": [
            device_schema_context
        ],
        "field1": "2022-05-11",
        "field2": "Is the Registrant also the owner of the Device? (provide evidence)",
        "field3": {
            "type": device_schema_field3_type,
            "@context": device_schema_field3_context
        },
        "field4": {
            "type": device_schema_field4_type,
            "@context": device_schema_field4_context,
            "field0": "Device Name",
            "field1": "Address",
            "field2": "Postal code",
            "field3": "Country",
            "field4": "Longitude",
            "field5": "Latitude",
            "field6": "TSO’s ID for measurement point",
            "field7": 1,
            "field8": 1,
            "field9": "2022-05-11",
            "field10": "Owner of the network to which the Production Device is connected and the voltage of that connection",
            "field11": "If the Production Device is not connected directly to the grid, specify the circumstances, and additional relevant meter registration numbers",
            "field12": "Expected form of volume evidence",
            "field13": "If other please specify"
        },
        "field5": {
            "type": device_schema_field5_type,
            "@context": device_schema_field5_context,
            "field0": "Energy Source (Input) – see Appendix 2",
            "field1": "Technology – see Appendix 2",
            "field2": true,
            "field3": "If yes give details",
            "field4": true,
            "field5": "If yes give details",
            "field6": "Please give details of how the site can import electricity by means other than through the meter(s) specified above",
            "field7": "Please give details (including registration id) of any carbon offset or energy tracking scheme for which the Production Device is registered. State ‘None’ if that is the case",
            "field8": "Please identify any labeling schemes for which the Device is accredited",
            "field9": true,
            "field10": "If public (government) funding has been received when did/will it finish?",
            "field11": "2022-05-11",
            "field12": "Preferred I-REC Device Verifier"
        }
    },
    "ref": preset
}; 

var presetObject = preset.document.credentialSubject[0];
for(var i = 0; i< presetFields.length; i++) {
    if(presetFields[i].value) {
        var value = presetObject[presetFields[i].value];
        if(typeof value == 'object') {
            value = Object.assign(value, body.document[presetFields[i].name]) 
        }
        body.document[presetFields[i].name] = value;
    }
}

outputs.body = JSON.stringify(body)


})(inputs, outputs);

