sys_variable_value.value:
(function execute(inputs, outputs) {

if (!Object.assign) {
  Object.defineProperty(Object, 'assign', {
    enumerable: false,
    configurable: true,
    writable: true,
    value: function(target) {
      'use strict';
      if (target === undefined || target === null) {
        throw new TypeError('Cannot convert first argument to object');
      }

      var to = Object(target);
      for (var i = 1; i < arguments.length; i++) {
        var nextSource = arguments[i];
        if (nextSource === undefined || nextSource === null) {
          continue;
        }
        nextSource = Object(nextSource);

        var keysArray = Object.keys(Object(nextSource));
        for (var nextIndex = 0, len = keysArray.length; nextIndex < len; nextIndex++) {
          var nextKey = keysArray[nextIndex];
          var desc = Object.getOwnPropertyDescriptor(nextSource, nextKey);
          if (desc !== undefined && desc.enumerable) {
            to[nextKey] = nextSource[nextKey];
          }
        }
      }
      return to;
    }
  });
}  

var username = inputs.username
var hederaAccountId = inputs.account_id

var response = JSON.parse(inputs.response)

var schema = response.schema
var presetFields = response.presetFields
var preset = JSON.parse(inputs.device_json)

var _issue_preset = JSON.stringify(preset)
var _issue_preset_fields = JSON.stringify(presetFields)

var _issue_schema_context = schema.contextURL
var _issue_schema_type = schema.type
var _issue_schema_field2_context = schema.fields[2].context.context
var _issue_schema_field2_type = schema.fields[2].context.type
var _issue_schema_field3_context = schema.fields[3].context.context
var _issue_schema_field3_type = schema.fields[3].context.type


var issue_preset = _issue_preset
var preset = JSON.parse(issue_preset)
var issue_presetFields = _issue_preset_fields
var presetFields = JSON.parse(issue_presetFields)

var issue_schema_context = _issue_schema_context
var issue_schema_type = _issue_schema_type

var issue_schema_field2_context = _issue_schema_field2_context
var issue_schema_field2_type = _issue_schema_field2_type
var issue_schema_field3_context = _issue_schema_field3_context
var issue_schema_field3_type = _issue_schema_field3_type

var body = {
    "document": {
        "type": issue_schema_type,
        "@context": [
            issue_schema_context
        ],
        "field0": inputs.registrant_did,
        "field1": inputs.device_did,
        "field2": {
            "type": issue_schema_field2_type,
            "@context": issue_schema_field2_context
        },
        "field3": {
            "type": issue_schema_field3_type,
            "@context": issue_schema_field3_context
        },
        "field4": "labeling scheme(s)",
        "field5": "2022-05-01",
        "field6": inputs.start_date,
        "field7": Number(inputs.kwh),
        "field8": inputs.end_date,
        "field9": 1,
        "field10": "Type a: Settlement Metering data",
        "field11": "Type b: Non-settlement Metering data",
        "field12": "Type c: Measured Volume Transfer documentation",
        "field13": "Type d: Other",
        "field14": true,
        "field15": true,
        "field16": true,
        "field17": "zmjoore4",
        "field18": "0.0.34743908",
    },
    "ref": preset
}

var presetObject = preset.document.credentialSubject[0];
for(var i = 0; i< presetFields.length; i++) {
    if(presetFields[i].value=='username') {
        body.document[presetFields[i].name] = username;
    } else if(presetFields[i].value=='hederaAccountId') {
        body.document[presetFields[i].name] = hederaAccountId;
    } else if(presetFields[i].value) {
        var value = presetObject[presetFields[i].value];
        if(typeof value == 'object') {
            value = Object.assign(value, body.document[presetFields[i].name]) 
        }
        body.document[presetFields[i].name] = value;
    }
}

outputs.body = JSON.stringify(body)

})(inputs, outputs);

